import { createSlice } from "@reduxjs/toolkit";


const ModifyNumberSlice = createSlice({

    name:'upAndDown',

    initialState:0,

    reducers:{
        increment(state,action){
            state = action.payload+1;
            return state;
        }
        ,
        decrement(state,action){
            return state-1;
        }
    }

})

export const {increment,decrement} = ModifyNumberSlice.actions;

export default ModifyNumberSlice.reducer;
