import React, { useState } from 'react'

/* App is parent and PropsFromChildToParent is child so pass props from child to parent */
const PropsFromChildToParent = () => {


  const [text,setText] = useState('');  

  const onHandler = (event) => {
    setText(event.target.value);
  }

  return (
    <div>
        <form class="form-inline">
                <div class="form-group mx-sm-3 mb-2">
                    <label for="inputPassword2" class="sr-only">Text</label>
                    <input type="text" class="form-control" placeholder="Text" value={text} onChange={onHandler} />
                </div>
                <button type="submit" class="btn btn-primary mb-2" >Text to uppercase</button>
        </form> 
    </div>
  )
}

export default PropsFromChildToParent
