const { createSlice } = require("@reduxjs/toolkit");

const STATUS = Object.freeze({
    IDLE : 'idle',
    ERROR : 'error',
    LOADING : 'loading'
})

const WorkWithAPI = createSlice({

    name :"Fake API Call",

    initialState : {
        data :[],
        status : STATUS.IDLE
    },

    reducers : {
        add(state,action){
            state.push(action.payload);
        }
    }
})

export const {add} =  WorkWithAPI.actions;

export default WorkWithAPI.reducer;
