import React, { useState } from 'react';
import AddNewSections from './components/AddNewSections';
import PropsFromChildToParent from './components/PropsFromChildToParent';
import PropsFromParentToChild from './components/PropsFromParentToChild';


function App() {

  // child to parent

  const [data,setData] = useState();
  const onchangeTextDateHandler= (updateText) => {
    setData(updateText);
  }

  return (
    // use fragments tags 
    <>
    {/* Add New sections  */}
      <AddNewSections/>
    <h2>-----------------------------------------------------------------------------------------------------------------------</h2>

    <div className='row'>
      <PropsFromChildToParent onchangeTextData={onchangeTextDateHandler} setAlert={onchangeTextDateHandler}/>
      <h5 style={{marginLeft:'20px'}}> Result In Parent from child  :  {data == Number(-1) ? "Please filled data" : data} </h5>
    </div>

    <h2>-----------------------------------------------------------------------------------------------------------------------</h2>

    {/* App is child and PropsFromParentToChild is aprent so pass props from parent to child */}
    <PropsFromParentToChild/>

    </>
  );
}

export default App;
