import React from 'react'
import { useCallback } from 'react'
import { useState } from 'react'

// The React useCallback Hook returns a memoized callback function.
//This allows us to isolate resource intensive functions so that they will not automatically run on every render.
//The useCallback Hook only runs when one of its dependencies update.
//This can improve performance.

const Diary = ({diary ,addDiary}) => {
    return(
        <>
        <h5>My diary : </h5>
            {
                diary.map((entry,i)=>{
                   return  <h5 key={i}>{entry + i}</h5>
                })
            }
        <button onClick={addDiary}>Click me</button>
        </>
    )
}

const UseCallbackHook = () => {

  const [diary,setDiary] = useState([]);
  const [val,setVal] = useState(0);
  
  const addDiary = useCallback(()=>{
    setDiary((prev)=>{
        return [...prev,'new Entry']
    });
  },[diary]);

  return (
    <div>
        <Diary diary={diary} addDiary={addDiary}/>
        <hr/>
        <button onClick={()=>{setVal(val+1)}}>Click : {val}</button>
    </div>
  )
}

export {UseCallbackHook,Diary}
