import { createSlice } from "@reduxjs/toolkit";

const BookSlice = createSlice({

    name:'Book Slice',

    initialState : {
        data :[]
    }
    ,
    reducers : { 
        addBook(state,action){
            state.data.push(action.payload);
        }
    }
})

export const {addBook} = BookSlice.actions;

export default BookSlice.reducer;
